DROP VIEW REAL_ESTATE_POSTS;
CREATE VIEW REAL_ESTATE_POSTS AS SELECT PDRE.POST_ID,PDRE.SUB_CATEGORY,PDRE.RENT_OR_SALE,PDRE.NEW_OR_RESALE,PDRE.READY_TO_OCCUPY,PDRE.EXPECTED_COMPLETION_DATE,PDRE.AREA,PDRE.LOCATION,PDRE.CITY,PDRE.PRICE_VALUE,PDRE.PRICE_UNIT,PDRE.FACING_DIRECTION,PDRE.MARITAL_PREFERENCE,PDRE.FOOD_PREFERENCE,PDRE.REGIONAL_PREFERENCE,PDRE.BEDROOMS,PDRE.AGE_VALUE,PDRE.AGE_UNIT,PDRE.FURNISHED,PDRE.FLOOR_NUMBER,PDRE.MAINTENANCE,PDRE.APPROVAL_AUTHORITY,PDRE.BUILDER_NAME,PDRE.SHARING,PDRE.GENDER,PDRE.NO_OF_ROOMMATES,PDRE.CAR_PARKING,PDRE.FOOD,PDRE.WIFI,PDRE.GYM,PDRE.CHILDREN_PLAY_AREA,PDRE.SWIMMING_POOL,PDRE.CREATED_ON,PDRE.CREATED_BY,PDRE.MODIFIED_ON,PDRE.MODIFIED_BY,PDRE.CLUB_HOUSE,PDRE.TV,PDRE.POWER_BACKUP,POIT.TITLE,POIT.CONTACT_NO,POIT.CONTACT_PERSON_NAME,POIT.DESCRIPTION,POIT.IMAGE_FILE_NAME,POIT.CORP_ID FROM PDRE,POIT where POIT.POST_ID = PDRE.POST_ID; 